mapper.select:
select
,
tsu.nick_name
from t_sys_notice tsn
LEFT JOIN t_sys_user tsu ON tsu.id = tsn.create_user
where 1=1

    AND tsn.title LIKE #{title}


    AND tsn.content LIKE #{content}


    AND tsn.level = #{level}


    AND tsu.id = #{createUser}


    and tsn.status = #{status,jdbcType=INTEGER}


    and tsn.create_time >= #{dateFrom,jdbcType=TIMESTAMP}


    and tsn.create_time < #{dateTo,jdbcType=TIMESTAMP}

ORDER BY ${orderBy}
