mapper.select:
select
DISTINCT

from t_sys_user tsu
LEFT JOIN t_sys_user_role tsur ON tsur.user_id = tsu.id
where 1=1

    AND tsu.user_name LIKE #{userName}


    AND tsu.nick_name LIKE #{nickName}


    AND tsu.real_name LIKE #{realName}


    AND tsu.mobile = #{mobile}


    AND tsu.email = #{email}


    AND tsur.role_id = #{role}


    and tsu.status = #{status,jdbcType=INTEGER}


    and tsu.create_time >= #{dateFrom,jdbcType=TIMESTAMP}


    and tsu.create_time < #{dateTo,jdbcType=TIMESTAMP}

ORDER BY ${orderBy}
